import React, {useState, useEffect} from "react";

const HeroMain = () => {
  const [mousePosition, setMousePosition] = useState({ x: 0, y: 0 });

  useEffect(() => {
    const handleMouseMove = (e) => {
      setMousePosition({
        x: e.clientX,
        y: e.clientY,
      });
    };

    window.addEventListener("mousemove", handleMouseMove);

    console.log()

    return () => window.removeEventListener("mousemove", handleMouseMove);
  }, []);

  const calculateEyeMovement = (eyeX, eyeY) => {
    const deltaX = mousePosition.x - eyeX;
    const deltaY = mousePosition.y - eyeY;
    const angle = Math.atan2(deltaY, deltaX) * (180 / Math.PI);
    return angle;
  };

  return (
    <div className="relative">
      <h1 className="text-[15em] text-green-600 tracking-tighter bg-transparent">
        Kode!
      </h1>
      <div className="w-[5.7em] h-[5.5em] bg-green-400 rounded-full absolute top-[10.3em] left-[10.15em] z-10" />
      <div className="w-[5.7em] h-[5.5em] bg-green-400 rounded-full absolute top-[10.3em] left-[19em] z-10" />
      <div
        className="w-[4em] h-[4em] bg-gray-900 rounded-full absolute top-[10.8em] left-[11em] z-10"
        style={{
          transform: `rotate(${calculateEyeMovement(11, 10.8)}deg)`,
          transition: "transform 0.1s ease",
        }}
      />
      <div
        className="w-[4em] h-[4em] bg-gray-900 rounded-full absolute top-[10.8em] left-[19.8em] z-10"
        style={{
          transform: `rotate(${calculateEyeMovement(19.8, 10.8)}deg)`,
          transition: "transform 0.1s ease",
        }}
      />
      <div className="w-[6.5em] h-[7.05em] border-8 border-green-600 rounded-full absolute top-[8.85em] left-[9.75em] z-10 " />
      <div className="w-[6.3em] h-[7.05em] border-8 border-green-600 rounded-full absolute top-[8.85em] left-[18.63em] z-10 " />
    </div>
  );
};

export default HeroMain;
