import React, { useRef } from "react";
import head from "../../../../../public/assets/Kody/head.png";
import body from "../../../../../public/assets/Kody/body.png";
import wingLeft from "../../../../../public/assets/Kody/wing-left.png";
import wingRight from "../../../../../public/assets/Kody/wing-right.png";
import footLeft from "../../../../../public/assets/Kody/foot-left.png";
import footRight from "../../../../../public/assets/Kody/foot-right.png";
import { useGSAP } from "@gsap/react";
import gsap from "gsap";

const AnimatedKody = () => {
  gsap.registerPlugin(useGSAP);

  const fullBodyRef = useRef();
  const wingRightRef = useRef();
  const wingLeftRef = useRef();

  useGSAP(() => {
    const tl = gsap.timeline();

    tl.to(wingLeftRef.current,{ rotate: 0, duration: 0.8, y: 0, x: 0, ease:'power2.inOut' },0)
      .to(wingRightRef.current, { rotate: 0, duration: 0.8, y: 0, x: 0, ease:'power2.inOut' }, 0)
      .to(fullBodyRef.current, { y: -50, duration: 1, ease:'power2.inOut' }, 0.1)
      .to(fullBodyRef.current, { y: -20, duration: 1, ease:'power2.inOut' })
      .to(wingLeftRef.current,{ rotate: 90, duration: 0.8, y: -40, x: -10, ease:'power2.inOut' },1.5)
      .to(wingRightRef.current, { rotate: -90, duration: 0.8, y: -40, x: 10, ease:'power2.inOut' },1.5)
      
      .to(fullBodyRef.current, { y: -50, duration: 1, ease:'power2.inOut' })
  }, []);

  return (
    <div
      className="relative h-[16.6em] w-[20em] mx-auto scale-110"
      ref={fullBodyRef}
    >
      <img
        Src={body}
        alt="kody"
        width={150}
        className="absolute top-[8.1em] left-1/2 -translate-x-1/2"
      />
      <img
        ref={wingLeftRef}
        src={wingLeft}
        alt="kody"
        width={90}
        className="absolute top-[8.5em] left-6 rotate-90 -translate-y-10 -translate-x-3"
      />
      <img
        ref={wingRightRef}
        src={wingRight}
        alt="kody"
        width={90}
        className="absolute top-[8.5em] right-6 -rotate-90 -translate-y-10 translate-x-3"
      />
      <img
        src={footLeft}
        alt="kody"
        width={50}
        className="absolute bottom-0 left-24"
      />
      <img
        src={footRight}
        alt="kody"
        width={50}
        className="absolute bottom-0 right-24"
      />
      <img
        src={head}
        alt="kody"
        width={200}
        className="absolute top-0 left-1/2 -translate-x-1/2"
      />
    </div>
  );
};

export default AnimatedKody;
