import React from "react";
import Button from "../../../../../components/Button";
import checkIcon from "../../../../../public/assets/icons/Check.png";

const pricingItems=[
    {
        people: 'individual',
        name: 'Starter',
        description: "Experience Kode!'s core features for free.",
        
    }
]

const PricingItem = ({item}) => {
  return (
    <div className="py-4 px-4 border-2 border-[#4F4F4F] w-[22em] bg-[#1E1E1E] rounded-lg">
      <div className="flex items-center justify-between">
        <div className="w-[3em] h-[3em] rounded-full border-4 border-white"></div>
        <div className="p-1 border-2 rounded-lg border-green-600">
          {item.people}
        </div>
      </div>
      <h1 className="mt-8 text-4xl">{item.name}</h1>
      <p className="text-gray-200 text-sm">
        {item.description}
      </p>
      <h1 className="mt-10 text-5xl">{item.price}</h1>
      <p className="text-[#808080]">{item.priceDesc}</p>
      <Button className="border-2 border-[#4F4F4F] bg-transparent mt-6 py-1">
        <p className="font-normal text-xl text-center ">{item.cta}</p>
      </Button>
      <div className="mt-6 flex flex-col gap-3">
        <div className="flex items-center gap-2 mx-2">
          <img src={checkIcon} />
          <p className="text-sm w-full text-gray-200">Access to a limited number of competitions per month</p>
        </div>
        <div className="flex items-center gap-2 mx-2">
          <img src={checkIcon} />
          <p className="text-sm w-full text-gray-200">Access to a limited number of competitions per month</p>
        </div>
        <div className="flex items-center gap-2 mx-2">
          <img src={checkIcon} />
          <p className="text-sm w-full text-gray-200">Access to a limited number of competitions per month</p>
        </div>
        <div className="flex items-center gap-2 mx-2">
          <img src={checkIcon} />
          <p className="text-sm w-full text-gray-200">Access to a limited number of competitions per month</p>
        </div>
      </div>
    </div>
  );
};

export default PricingItem;
